    ${tw`
        flex
        flex-col
        p-3
    `};
`;

const Title = styled.h1`
    ${tw`
        w-full
        text-center
        text-black
        text-2xl
        mb-2
        md:text-5xl
        font-extrabold
        md:font-black
        md:leading-normal
    `};
`;

const InfoText = styled.p`
    ${tw`
        // md:max-w-2xl
        text-sm
        md:text-base
        text-gray-500
        font-normal
        // mt-4

    `};
`;

const InfoTitle = styled.p`
    ${tw`
        text-lg
        md:text-base
        text-black
        font-normal

    `};
`;

