import React from "react";
import styled from "styled-components";
import tw from "twin.macro";
import Pareekshan2 from "../../../assets/images2/Pareekshan2.0.jpeg";
import Pareekshan1 from "../../../assets/images2/Pareekshan1.0.jpeg";
import EssayModule from "../../../assets/images2/EssayModuleNov21.jpg";
import { SCREENS } from "../../components/responsive";

const SectionContainer = styled.div`
  ${tw`
      flex
      flex-col
      md:m-20
      m-4
      mt-0
  `};
`;

const RowContainer = styled.div`
  ${tw`
      flex
      flex-col
      md:flex-row
  `};
`;

const CourseWrapper = styled.div`
  ${tw`
  `};
`;


const CourseContainer = styled.div`
  ${tw`
      flex
      flex-col
      m-2
      md:m-10
      p-2
      md:p-4
      rounded-md
      bg-blue-50
      shadow-md

  `};
`;

const ThumbnailContainer = styled.div`
//   widht: 100%;
//   height: auto;

//   img {
//     width: 100%;
//     height: 22.7em;
//   }

//   @media (min-width: ${SCREENS.md}) {
//     img {
//       width: 90%;
//       height: 24.9  em;
//       margin-left:auto;
//     margin-right:auto;
// }
// @media (min-width: ${SCREENS.lg}) {
//   img {
//     width: 90%;
//     height: 31.8em;
//     margin-left:auto;
//     margin-right:auto;
//   }
// }
// @media (min-width: ${SCREENS["2xl"]}) {
//     height:35em;
//     width: 100%;
//     margin-left:auto;
//     margin-right:auto;
// }
`;

const NameContainer = styled.h3`
  ${tw`
        md:text-2xl
        text-xl
        text-center
        font-bold
        text-black
        mt-1
        mb-1
    `};
`;

const InfoTextContainer = styled.h6`
  ${tw`
       text-gray-800
       md:text-lg
       text-center
       mt-3
       md: h-24
    `};
`;

export function CoursesInDetails() {
  return (
    <SectionContainer>
      <RowContainer> 

        <CourseWrapper> 
      <CourseContainer>
        <NameContainer>PAREEKSHAN 1.0</NameContainer>
        <ThumbnailContainer>
          <img src={Pareekshan1} alt="pareekshan1.0" />
        </ThumbnailContainer>
        <InfoTextContainer>
          Targetting UPSC 2022-23 for first time? then this module is for you.</InfoTextContainer>
      </CourseContainer>
      </CourseWrapper>

      <CourseWrapper>
      <CourseContainer>
        <NameContainer>PAREEKSHAN 2.0</NameContainer>
        <ThumbnailContainer>
          <img src={Pareekshan2} alt="Pareekshan2.0" />
        </ThumbnailContainer>
        <InfoTextContainer>
        This module is specially designed for those who could not clear prelims 2021. This is fast track version of Pareekshan1.0 
        </InfoTextContainer>
      </CourseContainer>
      </CourseWrapper>

        <CourseWrapper>
      <CourseContainer>
        <NameContainer>Essay Module</NameContainer>
        <ThumbnailContainer>
          <img src={EssayModule} alt="EssayModule" />
        </ThumbnailContainer>
        <InfoTextContainer>
          A special module designed for essay writing practice.
        </InfoTextContainer>
      </CourseContainer>
      </CourseWrapper>
      </RowContainer>

      <RowContainer> 
        <CourseWrapper>
      <CourseContainer>
        <NameContainer>GS-3 Test Series</NameContainer>
        <ThumbnailContainer>
          <img src={Pareekshan2} alt="Pareekshan2.0" />
        </ThumbnailContainer>
        <InfoTextContainer>
        A full length test series specially designed for GS3. 
        </InfoTextContainer>
      </CourseContainer>
      </CourseWrapper>

      <CourseWrapper>
      <CourseContainer>
        <NameContainer>Special Mains Batch</NameContainer>
        <ThumbnailContainer>
          <img src={Pareekshan2} alt="Pareekshan2.0" />
        </ThumbnailContainer>
        <InfoTextContainer>
        This is for students who have cleared Prelims2021. Focused mentoring will be given to a very limited admissions. 
        </InfoTextContainer>
      </CourseContainer>
      </CourseWrapper>

      <CourseWrapper>
      <CourseContainer>
        <NameContainer>
          Optional Courses
          </NameContainer>
        <ThumbnailContainer>
          <img src={Pareekshan2} alt="Pareekshan2.0" />
        </ThumbnailContainer>
        <InfoTextContainer>
        Comprehensive optional courses for Sociology, Geography and History in both mediums (Marathi and English).
        </InfoTextContainer>
      </CourseContainer>
      </CourseWrapper>
      </RowContainer>



    </SectionContainer>
  );
}
